import React from 'react'

import HearthSVG from './HearthSVG'
import './StoryCell.css'

const StoryCell = ({ story, handleClick}) => {
  const style = {
    width: '200px',
    height: '120px',
    background: `url(${story.image})`,
    backgroundSize: 'cover',
    margin: '10px',
    position: 'relative',
    cursor: 'pointer'
  }

  const svgStyle = {
    position: 'absolute',
    right: 5,
    top: 5
  }

  return (
    <div className='card'>
      <div className='card-img-wrapper'>
      <HearthSVG style={svgStyle} filled={story.liked} />
        {/* <img clasName='heart-img' src={require('../icons/baseline-favorite-24px.svg')} alt='heart-icon' /> */}
        <img className='card-img' src={story.image} alt={story.title} />
      </div>
      <div class='title-bar'>
        {story.title}
      </div>
    </div>

  )
}

export default StoryCell
