import React from 'react'

import HearthSVG from './HearthSVG'
import './StoryCell.css'

const StoryCell = ({ story, toggleLike, setSelectedStory }) => {
  const style = {
    width: '250px',
    height: '144px',
    background: `url(${story.image})`,
    backgroundSize: 'cover',
    position: 'relative',
    cursor: 'pointer'
  }

  const svgStyle = {
    position: 'absolute',
    right: 5,
    top: 5
  }

  return (
    <div className='card'>
      <div 
        style={style} 
        className='card-img-wrapper'
        onClick={() => setSelectedStory(story)}
      >
        <HearthSVG
          story={story}
          handleClick={() => toggleLike(story.id)}
          style={svgStyle}
          filled={story.liked}
        />
      </div>
      <div class='title-bar'>
        {story.title}
      </div>
    </div>

  )
}

export default StoryCell
