import React, { Component } from 'react'

import StorySearch from '../../components/StorySearch'
import StoryFilter from '../../containers/StoryFilter/StoryFilter'
import StoryContent from '../../components/StoryContent/StoryContent'
import Stories from '../Stories/Stories'
import './Digest.css'

const url = 'http://localhost:3001/api/v1/stories'

class Digest extends Component {
  state = {
    stories: [],
    searchedStories: [],
    searchQuery: undefined,
    selectedStory: undefined,
    filters: {
      websites: [],
      categories: []
    }
  }

  getStoriesFromAPI = () => {
    return fetch(url)
      .then(res => res.json())
  }

  componentDidMount () {
    this.getStoriesFromAPI()
      .then(allStories => {
        const uniqueWebsites = [...new Set(allStories.map(item => item.website.name))]
        const uniqueCategories = [...new Set(allStories.map(item => item.category.name))] // ES6 Spread Operator and Set return a UNIQUE array
        this.setState({
          stories: allStories,
          filters: {
            websites: uniqueWebsites,
            categories: uniqueCategories
          }
        })
      })
  }

  getSearchedStories = (searchQuery) => this.state.stories.filter(story => story.title.toLowerCase().includes(searchQuery.toLowerCase()))

  handleSearchInput = (event) => {
    this.setState({
      searchQuery: event.target.value
    })
  }

  getStories = () => {
    const { stories, searchQuery } = this.state
    if (searchQuery === undefined) {
      return stories
    } else {
      return this.getSearchedStories(searchQuery)
    }
  }

  areFiltersEmpty = () => {
    const { filters } = this.state
    return filters.websites.length === 0 && filters.categories.length === 0
  }

  filteredStories = () => {
    const { stories, filters } = this.state
    return this.areFiltersEmpty()
      ? this.getStories()
      : this.getStories().filter(story =>
        filters.websites.includes(story.website.name) &&
        filters.categories.includes(story.category.name)
      )
  }

  // toggleFilter = (event, id) => {
  //   // const filteredWebsite = this.state.filters.websites.map(website =>
  //   //   event.target.value === id ? { ...website, filterOn: !website.filterOn } : website
  //   // )
  //   // this.setState({ XXXXXXXXstories }) 
  // }

  toggleLike = (event, id) => {
    event.stopPropagation()
    const stories = this.state.stories.map(story =>
      story.id === id ? { ...story, liked: !story.liked } : story
    )
    this.setState({ stories })
  }

  setSelectedStory = story => {
    const app = document.querySelector('.App')
    app.classList.add('popup_is_shown')
    this.setState({ selectedStory: story })
  }

  getSelectedStory = story => this.state.stories.find(stateStory => stateStory.id === story.id)

  clearSelectedStory = () => {
    const app = document.querySelector('.App')
    app.classList.remove('popup_is_shown')

    this.setState({ selectedStory: undefined })
  }

  render () {
    console.log('selected story:', this.state.selectedStory)
    const { toggleLike, handleSearchInput, setSelectedStory, getStories, getSelectedStory, clearSelectedStory, filteredStories, toggleFilter } = this
    return (
      <div className='App'>
        <img src='' alt='' />
        <h1>NEWS DIGEST</h1>
        <div>
          <StorySearch
            style={{ margin: '0 auto', maxWidth: 800 }}
            handleSearchInput={handleSearchInput}
          />
          <button className='filter-btn'>FILTER</button>
          <button className='saved-stories-btn'>SAVED STORIES</button>
          <StoryFilter

            filterTags={this.state.filters}
            toggleFilter={toggleFilter}
          />
          <div className={this.state.selectedStory ? 'show_story content_wrapper' : 'content_wrapper'}>
            <Stories
              getStories={getStories}
              toggleLike={toggleLike}
              setSelectedStory={setSelectedStory}
            />
            <StoryContent
              selectedStory={this.state.selectedStory && getSelectedStory(this.state.selectedStory)}
              toggleLike={toggleLike}
              clearSelectedStory={clearSelectedStory}
            />
          </div>
        </div>
      </div>
    )
  }
}

export default Digest

