import React, { Component } from 'react'

import StorySearch from '../components/StorySearch'
// import StoryFilter from '../components/StoryFilter'
import StoryContent from '../components/StoryContent'
import Stories from './Stories'

const url = 'http://localhost:3001/api/v1/stories'

class Digest extends Component {
  state = {
    stories: [],
    searchedStories: [],
    searchQuery: undefined,
    selectedStory: undefined
  }

  getStoriesFromAPI = () => {
    return fetch(url)
      .then(res => res.json())
  }

  componentDidMount () {
    this.getStoriesFromAPI()
      .then(allStories => this.setState(
        {
          stories: allStories
        }
      ))
  }

  getSearchedStories = (searchQuery) => this.state.stories.filter(story => story.title.toLowerCase().includes(searchQuery.toLowerCase()))

  handleSearchInput = (event) => {
    this.setState({
      searchQuery: event.target.value
    })
  }

  getStories = () => {
    const { stories, searchQuery } = this.state
    if (searchQuery === undefined) {
      return stories
    } else {
      return this.getSearchedStories(searchQuery)
    }
  }

  toggleLike = id => {
    console.log(id)
    const stories = this.state.stories.map(story =>
      story.id === id ? { ...story, liked: !story.liked } : story
    )
    this.setState({ stories })
  }

  setSelectedStory = (story) => this.setState({ selectedStory: story })

  getSelectedStory = (story) => this.state.stories.find(stateStory => story === this.state.selectedStory)

  clearSelectedStory = () => this.setState({ selectedStory: undefined})

  render () {
    const { toggleLike, handleSearchInput, setSelectedStory, getStories, getSelectedStory } = this
    const { selectedStory } = this.state
    return (
      <div className='App'>
        {/* <StoryFilter /> */}
        {/* <Stories
            getStories={getStories}
            toggleLike={toggleLike}
            setSelectedStory={setSelectedStory}
          /> */}

        {selectedStory === undefined
          ? <div>
            <StorySearch
              style={{ margin: '0 auto', maxWidth: 800 }}
              handleSearchInput={handleSearchInput}
            />
            <Stories
              getStories={getStories}
              toggleLike={toggleLike}
              setSelectedStory={setSelectedStory}
            />
          </div>
          : <StoryContent
            toggleLike={toggleLike}
            selectedStory={getSelectedStory(selectedStory)}
          />
        }
      </div>
    )
  }
}

export default Digest
