import React from 'react'

import HearthSVG from './HearthSVG'
import './StoryCell.css'

const StoryCell = ({ story, handleClick }) => {
  const style = {
    width: '200px',
    height: '120px',
    background: `url(${story.image})`,
    backgroundSize: 'cover',
    margin: '10px',
    position: 'relative',
    cursor: 'pointer'
  }

  const svgStyle = {
    position: 'absolute',
    right: 5,
    top: 5
  }

  return (
    <div className='story'>
      <div onClick={handleClick} style={style} className='card'>
        <HearthSVG style={svgStyle} filled={story.liked} />
      </div>
      <div class='title-bar'>
        {story.title}
      </div>
    </div>

  )
}

export default StoryCell
