import React from 'react'

import HearthSVG from './HearthSVG'
import './StoryCell.css'

const StoryCell = ({ story, handleClick }) => {
  const style = {
    left: '50%',
    width: '100%',
    background: `url(${story.image})`,
    backgroundSize: 'cover',
    margin: '10px',
    position: 'absolute',
    cursor: 'pointer',

    // position: absolute,

    // transform: 'translate(-50%)'
    display: 'inline-block'
    // vertical-align: 'bottom'
  }

  const svgStyle = {
    position: 'absolute',
    right: 5,
    top: 5
  }

  return (
    <div className='card'>
      <div onClick={handleClick} style={style} className='card-img-wrapper'>
        <HearthSVG style={svgStyle} filled={story.liked} />
        {/* <img clasName='heart-img' src={require('../icons/baseline-favorite-24px.svg')} alt='heart-icon' /> */}
        {/* <img className='card-img' src={story.image} alt={story.title} /> */}
      </div>
      <div class='title-bar'>
        {story.title}
      </div>
    </div>

  )
}

export default StoryCell
